import { useState } from "react"

function Cards({id , name, info ,image , price}){
    const [readmore , setReadmore] = useState(true) ;
    let description = readmore?info:`${info.substring(0,200)}` ;

    return (
        <div>
            <img src={image} alt="" />
            <div>
                <h4>{price}</h4>
                <h4>{name}</h4>
            </div>
            <div>{description}</div>
        </div>
    )
}

export default Cards
